import React, { useState, useEffect } from 'react';
import MascotaForm from '../components/MascotaForm';
import { Container, ListGroup, Button, Row, Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom'; // Actualiza la importación para usar useNavigate

const MascotaPage = () => {
  const [mascotas, setMascotas] = useState([]);
  const [editingMascota, setEditingMascota] = useState(null);
  const navigate = useNavigate(); // Hook para redirigir

  useEffect(() => {
    // Ejemplo de datos estáticos o puedes usar una API para obtener datos reales
    setMascotas([
      // Lista inicial de mascotas
    ]);
  }, []);

  const addMascota = (mascota) => {
    setMascotas([...mascotas, mascota]);
    setEditingMascota(null);
  };

  const deleteMascota = (index) => {
    setMascotas(mascotas.filter((_, i) => i !== index));
  };

  const editMascota = (index) => {
    setEditingMascota({ ...mascotas[index], index });
  };

  const handleSave = (mascota) => {
    if (editingMascota) {
      const updatedMascotas = mascotas.map((m, i) => (i === editingMascota.index ? mascota : m));
      setMascotas(updatedMascotas);
      setEditingMascota(null);
    } else {
      addMascota(mascota);
    }
  };

  const handleCreatePropietario = () => {
    navigate('/propietarios'); // Redirige a la página de propietarios
  };

  return (
    <Container>
      <Row>
        <Col md={6}>
          <h1 className="my-4">Gestión de Mascotas</h1>
          <MascotaForm
            mascota={editingMascota}
            onSave={handleSave}
          />
          <Button variant="primary" onClick={handleCreatePropietario} className="my-4">
            Crear Nuevo Propietario
          </Button>
        </Col>
        <Col md={6}>
          <h1 className="my-4">Lista de Mascotas</h1>
          <ListGroup>
            {mascotas.map((mascota, index) => (
              <ListGroup.Item key={index}>
                <h5>{mascota.nombre}</h5>
                <p>Especie: {mascota.especie}</p>
                <p>Edad: {mascota.edad} años</p>
                <p>Raza: {mascota.raza}</p>
                <p>Peso: {mascota.peso} kg</p>
                <p>Procedimiento Realizado: {mascota.procedimiento}</p>
                <Button variant="warning" className="me-2" onClick={() => editMascota(index)}>Editar</Button>
                <Button variant="danger" onClick={() => deleteMascota(index)}>Eliminar</Button>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>
    </Container>
  );
};

export default MascotaPage;
