import React, { useState, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';

const MascotaForm = ({ mascota, onSave }) => {
  const [nombre, setNombre] = useState('');
  const [especie, setEspecie] = useState('');
  const [edad, setEdad] = useState('');
  const [raza, setRaza] = useState('');
  const [peso, setPeso] = useState('');
  const [procedimiento, setProcedimiento] = useState('');

  useEffect(() => {
    if (mascota) {
      setNombre(mascota.nombre);
      setEspecie(mascota.especie);
      setEdad(mascota.edad);
      setRaza(mascota.raza);
      setPeso(mascota.peso);
      setProcedimiento(mascota.procedimiento);
    }
  }, [mascota]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ nombre, especie, edad, raza, peso, procedimiento });
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group controlId="formNombre">
        <Form.Label>Nombre</Form.Label>
        <Form.Control
          type="text"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          required
        />
      </Form.Group>
      <Form.Group controlId="formEspecie">
        <Form.Label>Especie</Form.Label>
        <Form.Control
          type="text"
          value={especie}
          onChange={(e) => setEspecie(e.target.value)}
          required
        />
      </Form.Group>
      <Form.Group controlId="formEdad">
        <Form.Label>Edad</Form.Label>
        <Form.Control
          type="number"
          value={edad}
          onChange={(e) => setEdad(e.target.value)}
          required
        />
      </Form.Group>
      <Form.Group controlId="formRaza">
        <Form.Label>Raza</Form.Label>
        <Form.Control
          type="text"
          value={raza}
          onChange={(e) => setRaza(e.target.value)}
          required
        />
      </Form.Group>
      <Form.Group controlId="formPeso">
        <Form.Label>Peso</Form.Label>
        <Form.Control
          type="number"
          value={peso}
          onChange={(e) => setPeso(e.target.value)}
          required
        />
      </Form.Group>
      <Form.Group controlId="formProcedimiento">
        <Form.Label>Procedimiento Realizado</Form.Label>
        <Form.Control
          type="text"
          value={procedimiento}
          onChange={(e) => setProcedimiento(e.target.value)}
          required
        />
      </Form.Group>
      <Button variant="primary" type="submit">
        Guardar
      </Button>
    </Form>
  );
};

export default MascotaForm;
